import { useState } from "react";

import {
  TodoItemContainer,
  CheckboxInput,
  LabelContainer,
  CheckboxLabel,
  DeleteIconContainer,
  DeleteIcon,
  DeleteButton,
} from "./styledComponents";

const TaskItem = (props) => {
  const { taskDetails, deleteTask } = props;
  const { id, task } = taskDetails;

  const [isChecked, toggleTaskCompletion] = useState(false);
  const handleCheckboxChange = () => {
    toggleTaskCompletion(id);
    console(toggleTaskCompletion(id));
  };

  const onClickDeleteTask = () => {
    deleteTask(id);
  };

  return (
    <TodoItemContainer>
      <CheckboxInput
        type="checkbox"
        checked={isChecked}
        onChange={handleCheckboxChange}
      />
      <LabelContainer>
        <CheckboxLabel isChecked={isChecked}>{task}</CheckboxLabel>
        <DeleteIconContainer>
          <DeleteButton onClick={onClickDeleteTask}>
            <DeleteIcon src="https://assets.ccbp.in/frontend/react-js/money-manager/delete.png" />
          </DeleteButton>
        </DeleteIconContainer>
      </LabelContainer>
    </TodoItemContainer>
  );
};

export default TaskItem;
