import React, { useState, useEffect } from "react";

import { v4 as uuidv4 } from "uuid";

import TaskItem from "../TaskItem";

import TabBar from "../TabBar";

import {
  AppContainer,
  MainHeading,
  CreateTaskContainer,
  CreateTaskHeading,
  Span,
  TodoUserInput,
  Button,
  TasksList,
} from "./styledComponents";

const Todo = () => {
  const [task, setTask] = useState("");
  const [tasksList, setTasksList] = useState([]);

  useEffect(() => {
    const storedTasks = JSON.parse(localStorage.getItem("tasksList"));
    if (storedTasks) {
      setTasksList(storedTasks);
    }
  }, []);

  const saveItemToLocalStorage = (tasksList) => {
    localStorage.setItem("tasksList", JSON.stringify(tasksList));
  };

  const handleOnClickSave = () => {
    saveItemToLocalStorage(tasksList);
  };

  const onChangeTask = (event) => {
    setTask(event.target.value);
  };

  const onClickAddTask = () => {
    if (task === "") {
      alert("Please enter a valid task");
      return;
    }
    const newTask = {
      id: uuidv4(),
      task,
    };
    setTasksList([...tasksList, newTask]);
    setTask("");
  };

  const deleteTask = (taskId) => {
    const filteredTasks = tasksList.filter((eachTask) => {
      return eachTask.id !== taskId;
    });
    setTasksList(filteredTasks);
  };

  const renderTodoList = () => {
    return (
      <TasksList>
        {tasksList.map((eachTask) => (
          <TaskItem
            taskDetails={eachTask}
            key={eachTask.id}
            deleteTask={deleteTask}
          />
        ))}
      </TasksList>
    );
  };

  return (
    <AppContainer>
      <MainHeading>Todo</MainHeading>
      <CreateTaskContainer>
        <CreateTaskHeading>
          Create <Span>Task</Span>
        </CreateTaskHeading>
        <TodoUserInput
          type="text"
          placeholder="What needs to be done?"
          value={task}
          onChange={onChangeTask}
        />
        <Button type="submit" onClick={onClickAddTask}>
          Add
        </Button>
        <TabBar />
        {renderTodoList()}
        <Button onClick={handleOnClickSave}>Save</Button>
      </CreateTaskContainer>
    </AppContainer>
  );
};

export default Todo;
