import { TabBarContainer, TodoItem, TodoItemButton } from "./styledComponents";

const TabBar = (props) => {
  // const heading = "Tab Bar";
  const { changeActiveTab } = props;

  const handleClick = (tab) => {
    changeActiveTab(tab);
  };

  return (
    <TabBarContainer>
      <TodoItem>
        <TodoItemButton type="button" onClick={() => handleClick("all")}>
          All Todos
        </TodoItemButton>
      </TodoItem>
      <TodoItem>
        <TodoItemButton type="button">Active Todos</TodoItemButton>
      </TodoItem>
      <TodoItem>
        <TodoItemButton type="button">Completed Todos</TodoItemButton>
      </TodoItem>
    </TabBarContainer>
  );
};

export default TabBar;
